<?php

namespace App\Http\Controllers;

use Illuminate\Http\Request;
use Illuminate\Support\Facades\DB;
use App\evento;
use App\noticia;
use App\eventimages;


use App\Http\Resources\eventoCollection;

class eventoController extends Controller
{
    public function store(Request $request)
    {


        if(empty($request->file('imagen'))){


            $post = new evento([
                'titulo' => $request->get('titulo'),
                'descripcion' => $request->get('descripcion'),
                'imagen' => 'no hay imagen' ,
                'tablanombre' => $request->get('tabla')
            ]);


            $post->save();



            if(!empty($request->imagen2)){

                foreach($request->imagen2 as $foto){



                    $imagen10 = base64_encode(file_get_contents($foto));

                    $post3 = new eventimages([

                        'idimages' => $post->id ,
                        'eveimages' => $imagen10,

                    ]);

                    $post3->save();

                }

            }else{

                $post3 = new eventimages([

                    'idimages' => $post->id ,
                    'eveimages' => '',

                ]);

                $post3->save();




            }





            $users = DB::table('logins')->select('notification')->where('id', '=', 1)->first()->notification;


            $add = $users + 1;


            $noti = DB::table('logins')
                ->where('id', 1)
                ->update(['notification' => $add]);


            $post2 = new noticia([
                'titulo' => $request->get('titulo'),
                'tabla' => $request->get('tabla'),


            ]);


            $post2->save();

            return response()->json('Formulario Llenado Correctamento');

        }else{
            $image = base64_encode(file_get_contents($request->file('imagen')));


            $post = new evento([
                'titulo' => $request->get('titulo'),
                'descripcion' => $request->get('descripcion'),
                'imagen' => $image ,
                'tablanombre' => $request->get('tabla')



            ]);


            $post->save();


            if(!empty($request->imagen2)){

                foreach($request->imagen2 as $foto){



                    $imagen10 = base64_encode(file_get_contents($foto));

                    $post3 = new eventimages([

                        'idimages' => $post->id ,
                        'eveimages' => $imagen10,

                    ]);

                    $post3->save();

                }

            }else{

                $post3 = new eventimages([

                    'idimages' => $post->id ,
                    'eveimages' => '',

                ]);

                $post3->save();




            }





            $users = DB::table('logins')->select('notification')->where('id', '=', 1)->first()->notification;


            $add = $users + 1;


            $noti = DB::table('logins')
                ->where('id', 1)
                ->update(['notification' => $add]);


            $post2 = new noticia([
                'titulo' => $request->get('titulo'),
                'tabla' => $request->get('tabla'),


            ]);


            $post2->save();

            return response()->json('Formulario Llenado Correctamento');



        }


    }

    public function index()
    {

        return new eventoCollection(evento::all());


    }

    public function index2()
    {
        $users = DB::table('eventos')
            ->orderBy('id', 'desc')->limit(3)

            ->get();

        return response()->json($users);

    }



    public function index3($id, Request $request)
    {
        $table = DB::table('eventimages')->where('idimages', $id)->get();

        return response()->json($table);


    }


    public function update($id, Request $request)
    {

//        $image = base64_encode(file_get_contents($request->file('imagen')));



        $table= DB::table('eventos')
            ->where('id', $id)
            ->update(['titulo' => $request->get('titulo') ,
                    'descripcion' => $request->get('descripcion'),



                ]

            );

        return response()->json('Fila Actualizada Correctamente');
    }


    public function image($id, Request $request)
    {

        $image = base64_encode(file_get_contents($request->file('imagen')));



        $table= DB::table('eventos')
            ->where('id', $id)
            ->update([
                    'imagen' => $image,

                ]

            );

        return response()->json('Imagen Subida Correctamente');
    }


    public function image2($id, Request $request)
    {

        DB::table('eventimages')->where('idimages', '=', $id)->delete();


        foreach($request->imagen2 as $foto){



            $imagen10 = base64_encode(file_get_contents($foto));

            $post3 = new eventimages([

                'idimages' => $id ,
                'eveimages' => $imagen10,

            ]);

            $post3->save();




        }


        return response()->json('Imagenes Subida Correctamente');
    }

    public function delete($id)
    {

        DB::table('eventimages')->where('idimages', '=', $id)->delete();


        $post = evento::find($id);

        $post->delete();

        return response()->json('Eliminado satisfactoriamente');
    }





}
